import React from 'react';
import { Avatar, useChatContext } from 'stream-chat-react';

const DirectPreview = ({ channel, client }) => {
    const members = Object.values(channel.state.members).filter(({ user }) => user.id !== client.userID);

    return (
        <div className='channel-preview__item single'>
            <p>{members[0]?.user?.name || members[0]?.user?.id}</p>
        </div>
    )
}

const GroupPreview = ({ channel, course }) => {
    if (channel?.data?.course === "chem" && course === "chem") {
        return (
            <p className='channel-preview__item_chem'>
                # {channel?.data?.name || channel?.data?.id}
            </p>
        )
    } else if (channel?.data?.course === "math" && course === "math") {
        return (
            <p className='channel-preview__item_math'>
                # {channel?.data?.name || channel?.data?.id}
            </p>
        )
    } else if (channel?.data?.course === "prog" && course == "prog") {
        return (
            <p className='channel-preview__item_prog'>
                # {channel?.data?.name || channel?.data?.id}
            </p>
        )
    }
}

const PreviewHandler = ({ type, course, channel, client }) => {
    if (type === "team") {
        return <GroupPreview channel={channel} course={course} />
    } else {
        return <DirectPreview channel={channel} client={client} />
    }
}

const TeamChannelPreview = ({ course, setActiveChannel, setIsCreating, setIsEditing, setToggleContainer, channel, type }) => {
    const { channel: activeChannel, client } = useChatContext();

    return (
        <>
            <div className={
                channel?.id === activeChannel?.id
                    ? `channel-preview__wrapper__selected-${course}`
                    : `channel-preview__wrapper-${course}`
            }
            onClick={() => {
                setIsCreating(false)
                setIsEditing(false)
                setActiveChannel(channel)

                if (setToggleContainer) {
                    setToggleContainer((prevState) => !prevState)
                }
                console.log(channel)
            }}
            >
                <PreviewHandler channel={channel} type={type} client={client} course={course}/>
            </div>
        </>
    )
}

export default TeamChannelPreview
